import { useEffect, useState } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

import {getDataUsers} from '../../../api';

const Filters = ({dataPosts, setPosts}) => {
    const [dataUser, setDataUser] = useState([])

    const fetchGetUsers = async () => {
        const data = await getDataUsers();
        setDataUser(data);
    }

    const onChangeUser = (value) =>{
        console.log()
    }

    useEffect(()=>{
        fetchGetUsers()
    },[])

    return (
        <Row className='mt-5'>
            <Col>Filtros:</Col>
            <Col>
                <Form.Select aria-label="Seleccione un usuario">
                    <option>Seleccione un usuario</option>
                    {
                        dataUser?.map((res)=>(
                            <option value={res.id}>{res.name}</option>
                        ))
                    }
                </Form.Select>
            </Col>
            <Col>
                <div className="d-grid gap-2">
                    <Button variant="primary">Ordenar por Titulo</Button>
                </div>
            </Col>
            <Col>
                <Form.Control type="text" placeholder="Buscar texto en body" />
            </Col>
            <Col>
            <div className="d-grid gap-2">
                <Button variant="danger">Reinciar Filtros</Button>
                </div>
            </Col>
        </Row>
    );
}


export default Filters
